\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (A) at (0,4);
  \coordinate (B) at (-3,0);
  \coordinate (C) at (3,0);

  % Points D on AB, E on AC
  \coordinate (D) at ($(A)!0.5!(B)$);
  \coordinate (E) at ($(A)!0.5!(C)$);

  % Triangle edges
  \draw (A) -- (B);
  \draw (A) -- (C);
  \draw (B) -- (C);

  % Segments BE and CD
  \draw (B) -- (E);
  \draw (C) -- (D);


  % Labels
  \node[above] at (A) {A};
  \node[left] at (B) {B};
  \node[right] at (C) {C};
  \node[left] at (D) {D};
  \node[right] at (E) {E};
\end{tikzpicture}
