\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (A) at (0,4);
  \coordinate (B) at (-3,0);
  \coordinate (C) at (3,0);

  % Points on AB and AC
  \coordinate (D) at ($(A)!0.4!(B)$);
  \coordinate (E) at ($(A)!0.4!(C)$);
  \coordinate (F) at ($(A)!0.7!(B)$);
  \coordinate (G) at ($(B)!0.5!(C)$);

  % Triangle edges
  \draw (A) -- (B) -- (C) -- cycle;

  % Internal segments
  \draw (D) -- (E);
  \draw (D) -- (C);
  \draw (F) -- (G);

  % Labels
  \node[above] at (A) {A};
  \node[left] at (B) {B};
  \node[right] at (C) {C};
  \node[left] at (D) {D};
  \node[right] at (E) {E};
  \node[left] at (F) {F};
  \node[below] at (G) {G};
\end{tikzpicture}
