\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (A) at (0,3);
  \coordinate (B) at (-2,0);
  \coordinate (C) at (3,0);
  \coordinate (D) at (1,0); % on BC
  \coordinate (E) at ($(A)!0.5!(C)$);

  % \coordinate (O) at (0.8,1.2); % intersection of AD and BE

  % Triangle ABC
  \draw (A) -- (B) -- (C) -- cycle;

  % Segments AD and BE
  \draw[name path=AD] (A) -- (D);
  \draw[name path=BE] (B) -- (E);

  % Intersection coordinate (no hardcode)
  \path[name intersections={of=AD and BE, by=O}];

  % Segment OC
  \draw (O) -- (C);

  % Labels
  \node[above] at (A) {A};
  \node[left] at (B) {B};
  \node[right] at (C) {C};
  \node[below] at (D) {D};
  \node[right] at (E) {E};
  \node[above left] at (O) {O};
\end{tikzpicture}
