\begin{tikzpicture}[scale=1, line cap=round, line join=round]
  \usetikzlibrary{calc,intersections}

  % Rectangle ABCD
  \coordinate (A) at (-3,2);
  \coordinate (B) at (-3,-1.5);
  \coordinate (C) at (3,-1.5);
  \coordinate (D) at (3,2);

  % Point F on BC (closer to C)
  \coordinate (F) at ($(B)!0.75!(C)$);

  % Point E on CD (midway up)
  \coordinate (E) at ($(C)!0.5!(D)$);



  % Draw rectangle
  \draw[thick] (A) -- (B) -- (C) -- (D) -- cycle;

  % Solid segments
  \draw[thick] (A) -- (F);
  \draw[thick] (D) -- (F);
  \draw[dashed] (E) -- (F);
  \draw[dashed] (A) -- (E);

  % Dashed segments
  % Intersection H = (AE) ∩ (DF)
  \path[name path=AE] (A) -- (E);
  \path[name path=DF] (D) -- (F);
  \path[name intersections={of=AE and DF, by=H}];


  % Labels
  \node[above left]  at (A) {A};
  \node[left]        at (B) {B};
  \node[below right] at (C) {C};
  \node[above right] at (D) {D};
  \node[right]       at (E) {E};
  \node[below]       at (F) {F};
  \node[below left]  at (H) {H};

\end{tikzpicture}
