\usetikzlibrary{calc,intersections}

\begin{tikzpicture}[scale=1]
  \coordinate (A) at (-4,0);
  \coordinate (D) at (0,0);
  \coordinate (B) at (2,0);
  \coordinate (C) at (0,3);

  \draw (A)--(C)--(B)--cycle;
  \draw (C)--(D);

  % Diameter = BD => center M is midpoint of BD
  \coordinate (M) at ($(B)!0.5!(D)$);

  % radius number in cm (THIS avoids pt/cm confusion)
  \path let \p1=(B), \p2=(M) in
    \pgfmathsetmacro{\r}{veclen(\x1-\x2,\y1-\y2)/1cm};

  % named circle path + visible circle
  \path[name path=circ] (M) circle[radius=\r cm];
  \draw               (M) circle[radius=\r cm];

  % E = other intersection of circle and BC (not B)
  \path[name path=BC] (B)--(C);
  \path[name intersections={of=circ and BC, by={X,Y}, sort by=BC}];
  \coordinate (E) at (Y); % X is B, Y is the other one

  \foreach \P/\pos in {A/left,B/right,C/above,D/below,E/above right}{
    \fill (\P) circle (1.5pt);
    \node[\pos] at (\P) {$\P$};
  }
\end{tikzpicture}
