\begin{tikzpicture}[scale=1.5]
  % Coordinates
  \coordinate (C) at (0,0);
  \coordinate (A) at (4,0);

  % Calculate B based on your geometric constraints
  % Line CB is at 30 degrees from horizontal (90-60)
  % Line AB is at 135 degrees from horizontal (90+45)
  \path[name path=lineCB] (C) -- ++(30:5);
  \path[name path=lineAB] (A) -- ++(135:5);
  \path [name intersections={of=lineCB and lineAB, by=B}];

  % Draw Triangle
  \draw[thick] (A) -- (B) -- (C) -- cycle;

  % North lines
  \draw[dashed] (C) -- ++(0,1.8) node[above] {北};
  \draw[dashed] (A) -- ++(0,1.8) node[above] {北};
  \draw[dashed] (C) -- ++(0,-0.3);
  \draw[dashed] (A) -- ++(0,-0.3);

  % --- DYNAMIC ANGLE AT C (60°) ---
  \draw (C) let \p1 = ($(B)-(C)$), \n1 = {atan2(\y1,\x1)} in 
    (C) ++(90:0.7) arc (90:\n1:0.7)
    node at ($(C) + ({90+(\n1-90)/2}:0.9)$) {60$^\circ$}; % text at right

  % --- DYNAMIC ANGLE AT A (45°) ---
  % This part calculates the angle of line AB automatically
  \draw (A) let \p1 = ($(B)-(A)$), \n1 = {atan2(\y1,\x1)} in 
    (A) ++(90:0.7) arc (90:\n1:0.7)
    node at ($(A) + ({90+(\n1-90)/2}:0.9)$) {45$^\circ$}; % text at left

  % Points and Labels
  \node[below left] at (C) {$C$};
  \node[below right] at (A) {$A$};
  \node[above] at (B) {$B$};
\end{tikzpicture}
