% Adjusting the view angle
\tdplotsetmaincoords{70}{110}

\begin{tikzpicture}[tdplot_main_coords, scale=2, line join=round, line cap=round]

    % Define dimensions
    \def\w{1} % Width
    \def\d{1} % Depth
    \def\h{1} % Height

    % Define Coordinates
    \coordinate (O) at (0,0,0);
    \coordinate (C) at (0,-1,0);
    \coordinate (B) at (1,0,0);
    \coordinate (D) at (0,1,0);
    \coordinate (A) at (-1,0,0);
    \coordinate (E) at (0,0,1);


    % Draw Hidden Edges (Dashed)
    \draw[thick] (O) -- (A);
    \draw[thick] (O) -- (B);
    \draw[thick] (O) -- (C);
    \draw[thick] (O) -- (D);
    \draw[thick] (O) -- (E);



    % Label Vertices
    \node[anchor=south east] at (O) {$O$};
    \node[anchor=north east] at (A) {$A$};
    \node[anchor=north west] at (B) {$B$};
    \node[anchor=west] at (C) {$C$};
    \node[anchor=south east] at (D) {$D$};
    \node[anchor=east] at (E) {$E$};

\end{tikzpicture}
