\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (A) at (0,4);
  \coordinate (B) at (-3,0);
  \coordinate (C) at (2,0);
  \coordinate (D) at (0,0); % foot of perpendicular from A to BC
  \coordinate (E) at ($(A)!0.6!(C)$); % point on AC

  % Triangle ABC
  \draw (A) -- (B) -- (C) -- cycle;

  % Altitude AD
  \draw (A) -- (D);

  % Segment BE
  \draw (B) -- (E);

  % Right angle mark at D
  \draw (D) -- ++(0.3,0) -- ++(0,0.3) -- ++(-0.3,0);

  % Labels
  \node[above] at (A) {A};
  \node[left] at (B) {B};
  \node[right] at (C) {C};
  \node[below] at (D) {D};
  \node[right] at (E) {E};
\end{tikzpicture}
