\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (P) at (0,3);
  \coordinate (A) at (0,0);
  \coordinate (B) at (1.5,0);
  \coordinate (C) at (3.5,0);

  % Horizontal lines (top and bottom)
  \draw (-1,3) -- (4,3);
  \draw (-1,0) -- (4,0);

  % Segments from P to A, B, C
  \draw (P) -- (A);
  \draw (P) -- (B);
  \draw (P) -- (C);

  % Labels
  \node[above] at (P) {P};
  \node[below] at (A) {A};
  \node[below] at (B) {B};
  \node[below] at (C) {C};
\end{tikzpicture}
