\begin{tikzpicture}[scale=1.5]
  % 1. The Parallel Strip
  \draw[thick] (0,1) -- (5,1);     % Top line
  \draw[thick] (0,0.4) -- (5,0.4); % Bottom line
  \draw (0,0.4) -- (0,1);         % Left edge
  \draw (5,0.4) -- (5,1);         % Right edge

  % 2. Define Triangle Vertices
  % A is the vertex on the top line
  \coordinate (A) at (2.2,1); 
  % B is the bottom-left vertex
  \coordinate (B) at (1.0,-0.5);  
  % C is calculated to be 90 degrees clockwise from B (pointing bottom-right)
  \coordinate (C) at ($(A)!1.5!+90:(B)$);

  % % 3. Find intersection of AB with bottom line for Angle 2
  \coordinate (D) at (intersection of A--B and 0,0.4--5,0.4);

  % 4. Draw the Triangle
  \draw[thick] (B) -- (A) -- (C) -- cycle;

  % 5. PERPENDICULAR SIGN (The 90-degree square)
  % This draws the small square at the vertex A
  \pic [draw, thick, angle radius=3mm] {right angle = B--A--C};

  % 6. Angle 1: Between Top line (to the right) and AC
  \coordinate (R) at (5,1);
  \pic [draw, fill=gray!30, angle radius=5mm, "1" {shift={(0.4,0.15)}}] {angle = C--A--R};

  % 7. Angle 2: Between Bottom line (to the left) and AB
  \coordinate (L) at (0,0.4);
  \pic [draw, fill=gray!30, angle radius=5mm, "2" {shift={(-0.25,-0.1)}}] {angle = L--D--B};

\end{tikzpicture}
