\begin{tikzpicture}[scale=1]
  % Circle center O
  \coordinate (O) at (0,0);
  \draw (O) circle (2.0);

  % Diameter AB (horizontal)
  \coordinate (A) at (-2.0,0);
  \coordinate (B) at (2.0,0);

  % Chord CD intersecting AB at E
  % Put points on the circle by angle (relative to direction O->B)
  \coordinate (C) at ($(O)!1!135:(B)$);  % 135° point (x=-sqrt2, y=+sqrt2)
  \coordinate (D) at ($(O)!1!-65:(B)$);   % another points on the circle but not the diameter
  \path[name path=CD] (C) -- (D);
  \path[name path=AB] (A) -- (B);
  \path[name intersections={of=CD and AB, by=E}];

  % Draw segments
  \draw (A) -- (B);
  \draw (C) -- (D);

  % Points
  \fill (O) circle (1.5pt);

  % Labels
  \node[left]  at (A) {A};
  \node[right] at (B) {B};
  \node[above left]  at (C) {C};
  \node[below right] at (D) {D};
  \node[below] at (E) {E};
  \node[above right] at (O) {O};
\end{tikzpicture}
