\begin{tikzpicture}[scale=1]
  % Circle center O
  \coordinate (O) at (0,0);

  % Points on circle (approximate equilateral triangle inscribed)
  \coordinate (A) at (0,2);
  \coordinate (B) at (-1.732,-1); % approx -√3, -1
  \coordinate (C) at (1.732,-1);  % approx √3, -1

  % Draw circle
  \draw (O) circle (2);

  % Draw triangle ABC
  \draw (A) -- (B) -- (C) -- cycle;

  % Mark center O
  \fill (O) circle (1.5pt);
  \node[above right] at (O) {O};


  % Labels
  \node[above] at (A) {A};
  \node[left] at (B) {B};
  \node[right] at (C) {C};
\end{tikzpicture}
