  % Coordinates
  \coordinate (A) at (0,0);
  \coordinate (D) at (4,0);
  \coordinate (E) at (5,0);
  \coordinate (B) at (1,2);
  \coordinate (C) at (3,2);

  % Lines
  \draw (A) -- (B) -- (C) -- (D) -- (A);
  \draw (B) -- (D);
  \draw (D) -- (E);

  % Points (only A to D are visible)
  \fill (A) circle (1.5pt);
  \fill (B) circle (1.5pt);
  \fill (C) circle (1.5pt);
  \fill (D) circle (1.5pt);

  % Labels
  \node[below left] at (A) {A};
  \node[above left] at (B) {B};
  \node[above right] at (C) {C};
  \node[below right] at (D) {D};
  \node[below right] at (E) {E};

\end{tikzpicture}
