% Adjusting the view angle
\tdplotsetmaincoords{70}{110}

\begin{tikzpicture}[tdplot_main_coords, scale=2, line join=round, line cap=round]

    % Define dimensions
    \def\w{1} % Width
    \def\d{1.2} % Depth
    \def\h{2} % Height

    % Define Coordinates
    \coordinate (A) at (\w,0,0);
    \coordinate (B) at (\w,\d,0);
    \coordinate (C) at (0,\d,0);
    \coordinate (D) at (0,0,0);
    \coordinate (A1) at (\w,0,\h);
    \coordinate (B1) at (\w,\d,\h);
    \coordinate (C1) at (0,\d,\h);
    \coordinate (D1) at (0,0,\h);

    % Draw Hidden Edges (Dashed)
    \draw[dashed] (D) -- (A);
    \draw[dashed] (D) -- (C);
    \draw[dashed] (D) -- (D1);
    \draw[dashed] (A) -- (C); % Hidden diagonal edge

    % Draw Visible Edges of the Prism
    \draw[thick] (A) -- (B) -- (C);
    \draw[thick] (A1) -- (B1) -- (C1) -- (D1) -- cycle;
    \draw[thick] (A) -- (A1);
    \draw[thick] (B) -- (B1);
    \draw[thick] (C) -- (C1);

    % Draw Diagonal Plane Edges (A-B-C1-D1)
    \draw[thick] (B) -- (C1);
    \draw[thick] (A) -- (B); % Already drawn but keeping for logic

    % Label Vertices
    \node[anchor=north east] at (A) {$A$};
    \node[anchor=north west] at (B) {$B$};
    \node[anchor=west] at (C) {$C$};
    \node[anchor=south east] at (D) {$D$};
    \node[anchor=east] at (A1) {$A_1$};
    \node[anchor=north west] at (B1) {$B_1$};
    \node[anchor=south west] at (C1) {$C_1$};
    \node[anchor=south east] at (D1) {$D_1$};

\end{tikzpicture}
