% Set view: 70 degrees from zenith, 110 degrees rotation
\tdplotsetmaincoords{70}{110}

\begin{tikzpicture}[tdplot_main_coords, scale=0.8, line join=round, line cap=round]

  % Dimensions
  \pgfmathsetmacro{\w}{7} % Width (AB)
  \pgfmathsetmacro{\l}{5} % Length (BC)
  \pgfmathsetmacro{\h}{7} % Height of apex P

  % Base Coordinates (on the XY plane, z=0)
  \coordinate (A) at (0, 0, 0);
  \coordinate (B) at (\w, 0, 0);
  \coordinate (C) at (\w, \l, 0);
  \coordinate (D) at (0, \l, 0);

  % Apex P (centered above the base)
  \coordinate (P) at (\w/2, \l/2, \h);

  % --- 1. Draw Hidden Edges (Dashed) ---
  \draw[dashed] (A) -- (B);
  \draw[dashed] (A) -- (D);
  \draw[dashed] (A) -- (P);
  \draw[dashed] (A) -- (C);

  % --- 2. Draw Visible Faces/Edges ---
  \draw[thick, name path=BC] (B) -- (C);
  \draw[thick] (C) -- (D); % Base visible edges
  \draw[thick, name path=PB] (P) -- (B);
  \draw[thick] (P) -- (C);
  \draw[thick] (P) -- (D);

  \coordinate (E) at ($(B)!0.5!(P)$);
  \coordinate (F) at ($(B)!0.3!(C)$);
  \coordinate (G) at (\w/2, \l/2, \h/2); % Not sure, floating on the air

  \draw[dashed] (A) -- (E);
  \draw[dashed] (A) -- (F);
  \draw[dashed] (A) -- (G);
  \draw[solid] (C) -- (E);

  % Labels
  \node[left] at (A) {A};
  \node[below left] at (B) {B};
  \node[right] at (C) {C};
  \node[right] at (D) {D};
  \node[above] at (P) {P};
  \node[left] at (E) {E};
  \node[below] at (F) {F};
  \node[above] at (G) {G};

\end{tikzpicture}
