\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (A) at (0,4);
  \coordinate (B) at (-2,0);
  \coordinate (C) at (2,0);
  \draw (A) -- (B) -- (C) -- cycle;

  \coordinate (D) at ($(A)!0.6!(C)$);
  \coordinate (E) at ($(A)!0.5!(B)$);

  \coordinate (D_ext) at ($(E)!1.4!(D)$);
  \coordinate (E_ext) at ($(D)!1.4!(E)$);

  \draw (D_ext) -- (E_ext);

  % Segment BD
  \draw (B) -- (D);


  % Labels
  \node[above] at (A) {A};
  \node[left] at (B) {B};
  \node[right] at (C) {C};
  \node[above right] at (D) {D};
  \node[above left] at (E) {E};
\end{tikzpicture}
