\begin{tikzpicture}[scale=1]
  % Define square ABCD
  \coordinate (A) at (0,0);
  \coordinate (B) at (4,0);
  \coordinate (C) at (4,4);
  \coordinate (D) at (0,4);

  % Define points M and N
  \coordinate (M) at (4,2); % midpoint of BC
  \coordinate (N) at (2,4); % midpoint of CD

  % Draw square
  \draw (A) -- (B) -- (C) -- (D) -- cycle;

  % Draw diagonals and other segments
  \draw (A) -- (C); % diagonal AC
  \draw (B) -- (D); % diagonal BD
  \draw (A) -- (M); % AM
  \draw (B) -- (N); % BN


  % Labels
  \node[below left] at (A) {A};
  \node[below right] at (B) {B};
  \node[above right] at (C) {C};
  \node[above left] at (D) {D};
  \node[right] at (M) {M};
  \node[above] at (N) {N};
\end{tikzpicture}
