\begin{tikzpicture}[scale=0.8, >=Stealth]

  % --- LEFT VIEW (RECTANGLE) ---
  \draw[thick] (0,0) rectangle (1.5,3);

  % Vertical Dimension (7)
  \draw[|<->|] (-0.8,0) -- (-0.8,3) node[midway, left] {7};

  % Labels (Bottom)
  \node at (0.75, -1.25) {正视图};

  % --- RIGHT VIEW (RECTANGLE) ---
  \begin{scope}[shift={(4,0)}]
      \draw[thick] (0,0) rectangle (1.5,3);

      % Horizontal Dimension (2\sqrt{3})
      \draw[|<->|] (0,-0.3) -- (1.5,-0.3) node[midway, below] {$2\sqrt{3}$};

      % Label (Bottom)
      \node at (0.75, -1.25) {左视图};
  \end{scope}

  % --- BOTTOM VIEW (TRIANGLE) ---
  \begin{scope}[shift={(3.4,-4)}]
      % Drawing an equilateral-style triangle
      \draw[thick] (0,0) -- (2,0) -- (1,1.732) -- cycle;

      % Label (Bottom)
      \node at (1, -0.5) {俯视图};
  \end{scope}

\end{tikzpicture}
