\begin{tikzpicture}[scale=1]
  % Circle center O
  \coordinate (O) at (0,0);
  \draw[name path=circle] (O) circle (2);

  % Points on circle: D, B, C (in order around circle)
% 2. Define Points on circle: D, B, C
  \coordinate (D) at (-2,0);      % leftmost point
  \coordinate (B) at (-1.2,-1.6); % lower left

  % 3. Define Point P: outside circle, on line OC extended
  \coordinate (P) at ($(D)!2.5!(O)$);

  % Define PO
  \path[name path=linePO] (P) -- (O);
  \fill [name intersections={of=circle and linePO, by=C}];

  % 4. Define path for segment BP
  \path[name path=BP_line] (B) -- (P);

  % 5. Find intersection of BP and Circle O
  % This will find two points (the original B and the new A) (we'll drop one point that is just B)
  \fill [name intersections={of=BP_line and circle, by={B_alias, A}}];

  % Draw segments
  \draw (D) -- (C);  % chord DC
  \draw (P) -- (B);  % secant PB
  \draw (P) -- (C);  % segment PC (part of line PC)

  % Points
  \fill (O) circle (1.5pt);

  % Labels
  \node[above left] at (O) {O};
  \node[left] at (D) {D};
  \node[below] at (B) {B};
  \node[above right] at (C) {C};
  \node[below] at (A) {A};
  \node[above right] at (P) {P};
\end{tikzpicture}
