\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (A) at (0,3);
  \coordinate (B) at (2.5,0);
  \coordinate (C) at (1.8,1.5);
  \coordinate (S) at (0,0);

  % Triangle edges
  \draw (A) -- (B);
  \draw (A) -- (C);
  \draw (B) -- (C);
  \draw (A) -- (S);
  \draw (S) -- (B);

  % Dashed line SC
  \draw[dashed] (S) -- (C);

  % Labels
  \node[above] at (A) {A};
  \node[right] at (B) {B};
  \node[right] at (C) {C};
  \node[left] at (S) {S};
\end{tikzpicture}
