% Set the viewing angle
\tdplotsetmaincoords{70}{120}

\begin{tikzpicture}[tdplot_main_coords, scale=2.5, line join=round, line cap=round]

  % 1. Define Base Vertices (Ground Level)
  \coordinate (A) at (2,0,0);
  \coordinate (B) at (2,2,0);
  \coordinate (C) at (0,2,0);
  \coordinate (D) at (0,0,0);

  % 2. Define Top Vertices (Height = 2)
  \coordinate (A') at (2,0,2);
  \coordinate (B') at (2,2,2);
  \coordinate (C') at (0,2,2);
  \coordinate (D') at (0,0,2);

  % 3. Define the specific points on edges
  % Q is on D'C' (let's put it at the midpoint)
  \coordinate (Q) at ($(D')!0.5!(C')$);

  % E and F are on the base (near A and B)
  \coordinate (E) at (2, 0.5, 0); % Point on AB
  \coordinate (F) at (2, 1.5, 0); % Another point on AB or BC

  % 4. Draw Back Edges (Dashed for depth)
  \draw[dashed, gray] (D) -- (A);
  \draw[dashed, gray] (D) -- (C);
  \draw[dashed, gray] (D) -- (D');



  % 5. Draw Visible Edges
  \draw[thick] (A) -- (B) -- (C);
  \draw[thick] (A') -- (B') -- (C') -- (D') -- cycle;
  \draw[thick] (A) -- (A');
  \draw[thick] (B) -- (B');
  \draw[thick] (C) -- (C');

  % 6. Draw Internal Connections (the "geometry" part)
  \draw[blue, thick] (Q) -- (A') -- (E) -- cycle;
  \draw[blue, dashed] (A') -- (F) -- (Q);


  \node[anchor=north] at (A) {A};
  \node[anchor=north west] at (B) {B};
  \node[anchor=west] at (C) {C};
  \node[anchor=north] at (D) {D};
  \node[anchor=south east] at (A') {A'};
  \node[anchor=south west] at (B') {B'};
  \node[anchor=south] at (C') {C'};
  \node[anchor=south east] at (D') {D'};

  \node[anchor=south] at (Q) {Q};
  \node[anchor=north] at (E) {E};
  \node[anchor=north] at (F) {F};

\end{tikzpicture}
