\begin{tikzpicture}[scale=1]
  % Coordinate system
  \draw[->] (-1,0) -- (4,0) node[right] {$x$};
  \draw[->] (0,-2) -- (0,3) node[above] {$y$};

  % Circle centered at M
  \coordinate (T) at (1,0);
  \draw (T) circle (1.5);
  \coordinate (O) at (0,0);

  % Points on circle
  \coordinate (A) at ($(T) + (120:1.5)$);
  \coordinate (B) at ($(T) + (300:1.5)$);

  \coordinate (A_ext) at ($(B)!1.1!(A)$);
  \coordinate (B_ext) at ($(A)!1.1!(B)$);

  \coordinate (P) at ($(A)!0.4!(B)$);
  \coordinate (M) at ($(A)!0.6!(B)$);


  % Line AB passing through O
  \draw (A_ext) -- (B_ext);

  % Draw segments
  \draw (O) -- (P);
  \draw (O) -- (M);

  % Labels
  \node[above right] at (A) {$A$};
  \node[below right] at (B) {$B$};
  \node[below left] at (O) {$O$};
  \node[below] at (M) {$M$};
  \node[above right] at (P) {$P$};
\end{tikzpicture}
