\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (A) at (2.0, 3.0);
  \coordinate (B) at (-2.0, 1.0);
  \coordinate (C) at (3.0, -1.0);
  \coordinate (D) at (-1.0, -0.5);
  \coordinate (F) at ($(A)!0.6!(B)$);

  % Draw segments
  \draw[name path=AB] (A) -- (B);
  \draw (A) -- (C);
  \draw (B) -- (C);
  \draw (B) -- (D);
  \draw[name path=AD] (A) -- (D);
  \draw[name path=CF] (C) -- (F);

  \path [name intersections={of=AD and CF, by=E}];

  % Labels
  \node[above right] at (A) {A};
  \node[left] at (B) {B};
  \node[right] at (C) {C};
  \node[below left] at (D) {D};
  \node[above] at (F) {F};
  \node[right] at (E) {E};
\end{tikzpicture}
