\begin{tikzpicture}[scale=1]
  % Circle center O
  \coordinate (O) at (0,0);
  \coordinate (O) at (0,0);
  \def\R{2.5}
  \draw (O) circle (\R);

  % Define points using angles (0 is East, 90 is North)
  \coordinate (A) at (126.87:\R); % This is roughly (-1.5, 2.0)
  \coordinate (B) at (210:\R);    % Bottom-left
  \coordinate (C) at (40:\R);     % Top-right


  % Draw triangle ABC and radii
  \draw (A) -- (B) -- (C) -- cycle;
  \draw (O) -- (A);
  \draw (O) -- (C);

  % Points
  \fill (O) circle (1.5pt);

  % Labels
  \node[above left] at (A) {A};
  \node[below left] at (B) {B};
  \node[above right] at (C) {C};
  \node[below] at (O) {O};
\end{tikzpicture}
