\begin{tikzpicture}[scale=1]
  % Coordinates
  \coordinate (A) at (0,0);
  \coordinate (D) at (-2,0);
  \coordinate (B) at (0,3);
  \coordinate (C) at (-2,3);
  \coordinate (B') at (2,0);
  \coordinate (C') at (2,2);
  \coordinate (D') at (0,2);

  % Rectangle ABCD (original)
  \draw[thick] (A) -- (B) -- (C) -- (D) -- cycle;

  % Rectangle AB'C'D' (reflected/folded)
  \draw[thick] (A) -- (B') -- (C') -- (D') -- cycle;

  % Diagonal and folding lines
  \draw[thick] (A) -- (C);
  \draw[thick] (A) -- (C');
  \draw[dashed] (C) -- (C');

  % Labels
  \node[below left] at (D) {D};
  \node[above left] at (C) {C};
  \node[above right] at (B) {B};
  \node[below right] at (A) {A};
  \node[below right] at (B') {B'};
  \node[above right] at (C') {C'};
  \node[above left] at (D') {D'};
\end{tikzpicture}
