\begin{tikzpicture}[scale=0.8]
  % 1. Define Coordinates using Polar for F (perfect equilateral)
  \coordinate (G) at (0,0);
  \coordinate (H) at (12,0);
  \coordinate (F) at (60:12); % Radius 12 at 60 degrees from G

  % 2. Draw the main triangle
  \draw[thick, blue] (G) -- (H) -- (F) -- cycle;

  % 3. Angle marks using the 'angles' library for precision
  \pic [draw=none, angle radius=1cm, "$60^\circ$" font=\small] {angle = H--G--F};
  \pic [draw=none, angle radius=1cm, "$60^\circ$" font=\small] {angle = G--F--H};

  % 4. Side length labels (placed offset from the lines)
  \node[below=5pt] at ($(G)!0.5!(H)$) {12};

  % 5. Vertex Labels
  \node[below left] at (G) {G};
  \node[below right] at (H) {H};
  \node[above] at (F) {F};

  % 6. Points
  % \foreach \p in {G,H,F} \fill[blue] (\p) circle (2pt);

\end{tikzpicture}
